SET NAMES UTF8;
DROP DATABASE IF EXISTS ulife;
CREATE DATABASE ulife CHARSET=UTF8;
USE ulife;
/*用户表*/
CREATE TABLE user (
	#userId
	#电话
	#密码
	#昵称
	#性别   M-男  F-女   默认
	#年龄
	#学历
	#职业
	userId INT PRIMARY KEY AUTO_INCREMENT,
	phone VARCHAR(11),
	uwd VARCHAR(32),
	nickname VARCHAR(32) NOT NULL DEFAULT '',
	sex VARCHAR(1) NOT NULL DEFAULT 'M',
	age VARCHAR(3) NOT NULL DEFAULT '',
	edu VARCHAR(16) NOT NULL DEFAULT '',
	job VARCHAR(16) NOT NULL DEFAULT ''
);


/*一级区域*/
CREATE TABLE area(
	#一级区域ID
	#一级区域名称
	areaId INT PRIMARY KEY AUTO_INCREMENT,
	areaName VARCHAR(32)
);

/*二级区域*/
CREATE TABLE subArea(
	#二级区域ID
	#二级区域名称
	#一级区域ID
	subAreaId INT PRIMARY KEY AUTO_INCREMENT,
	subAreaName VARCHAR(32),
	areaId INT
);

/*经纪人*/
CREATE TABLE agent(
	#id
	#姓名
	#电话
	#图片
	#一级区域
	#二级区域
	agentID INT PRIMARY KEY AUTO_INCREMENT,
	agentName VARCHAR(16),
	agentPhone VARCHAR(11),
	areaId INT,
	subAreaId INT
);



/*个人收藏*/
CREATE TABLE favorite (
	#id
	#用户id
	#房源ID
	#收藏时间
	favoriteId INT PRIMARY KEY AUTO_INCREMENT,
	userId INT,
	zfId INT,
	fTime VARCHAR(16)
);


/*房源信息*/
CREATE TABLE zufang (
	zfId INT PRIMARY KEY AUTO_INCREMENT,#ID
	title VARCHAR(32) NOT NULL DEFAULT '',#标题
	pubTime BIGINT,#发布时间
	price INT,#价格
	houseType VARCHAR(16),#房型
	size INT,#面积
	floor VARCHAR(16),#六层
	leaseWay VARCHAR(16),#租赁方式
	pay VARCHAR(16),#支付方式
	orientation VARCHAR(16),#朝向
	decoration VARCHAR(16),#装修
	areaId INT,#一级区域ID
	subArea INT,#二级区域ID
	community VARCHAR(32),#小区
	address VARCHAR(32),#位置
	agentId INT,#经纪人ID
	details VARCHAR(32)  /*详情*/	
);

/*房源图片*/
CREATE TABLE zfImg (
	#id
	#zufangId
	#src
	img_id INT PRIMARY KEY AUTO_INCREMENT,
	zfId INT,
	zf_src VARCHAR(32)
);


/*新闻表*/
CREATE TABLE news(
	newsId INT PRIMARY KEY AUTO_INCREMENT,#newsId
	title VARCHAR(32) NOT NULL DEFAULT '',#标题
	pubTime BIGINT,#发布时间
	pic VARCHAR(64),#图片
	description VARCHAR(128),#摘要
	content VARCHAR(2048)#内容
);


